let temps = global.get('temperatures');
let mean_temp = global.get('mean_temp');

let resid = {}
let sum = 0

for (let key in temps) {
    let r = temps[key] - mean_temp;
    if (r<0) {
        r = -r;
        sum += r;
    }
    else {
        r=0;
    }
    resid[key] = r;
}

for (let key in resid) {
    resid[key]/=sum;
}

let power = msg.payload;
msg = [];

for (i = 1; i <11; i++) {
    p = power* resid['TC'+i]
    msg.push({topic:'TC'+i, payload:p})
}

return msg;

