var positions = flow.get('positions');

if (msg.topic == 'PositionChange') {

    if (msg.label in positions) {
        position = positions[msg.label]
    }
    else {
        position = 0;
    }

    // 2 mm pole length
    // 4096 interpolated steps
    // RLM2HDA12B

    var positionChange = -msg.payload.positionChange*2/4096;

    position += positionChange;

    positions[msg.label] = position;

    flow.set('positions', positions);

    var velocity = positionChange/msg.payload.timeChange;

    var pos = { topic: msg.label,  payload: position }
    var vel = { topic: msg.label,  payload: velocity }

    return [pos, vel]
}
else if (msg.topic == 'setZero') { 
    for (var label in positions) {
        positions[label]=0
    }

    flow.set('positions', positions);
}

