var position = Math.round(flow.get('positions').ARM2*10)/10;
var destination = msg.payload;

var distance = Math.abs(destination-position);

//this is the velocity of the setpoint
var velocity = 0.1; // mm/step

var nsteps = distance/velocity;

var interval = 1000; // ms/step

var duration = nsteps*interval;

msg.payload = { from:position, 
                to:destination, 
                duration:duration, 
                interval:interval };

return msg;
