var topic = 'setVoltage';
var p_min = flow.get('p_min');
var p_max = flow.get('p_max');
var power = msg.payload;

var p_band = p_max-p_min;

var v1 = p_min + p_band * power;
var v2 = p_min + p_band * (1-power)

var arm1 = {topic:topic, payload:{voltage:v1}};
var arm2 = {topic:topic, payload:{voltage:v2}};

return [arm1,arm2];

