func:
var pressures = global.get('pressures');

pressures[msg.topic] = msg.payload;

return msg

if (flow.get('connected')) {
    let temps = global.get('temperatures');
    let ht_power = global.get('ht_power');

    let mean_temp = global.get('mean_temp');
    let max_temp = Math.max.apply(null, Object.values(temps));

    let total_power = 0;

    let power = msg.payload;

    msg = [];

    if( max_temp < flow.get('setpoint')-10) {
        // Go fast when cold

        for (i = 1; i <11; i++) {
            msg.push({topic:'TC'+i, payload:power*(400/ht_power['HT'+i])/3.2 });
        }

        node.status({fill:"green",shape:"ring",text:"preheating"});
    }
    else {
        // Go even when close


        let resid = {}
        let sum = 0

        for (let key in temps) {
            let r = temps[key] - mean_temp;
            if (r<0) {
                r = -r
                //r = r*r;
                let ht = 'HT'+key.substring(2);
                sum += r * (400/ht_power[ht] );
            }
            else {
                r=0;
            }
            resid[key] = r;
        }

        for (let key in resid) {
            resid[key]/=sum;
        }

        resid['TC3']=0;

        for (i = 1; i <11; i++) {
            p = power* resid['TC'+i]
            msg.push({topic:'TC'+i, payload:p})
        }
        node.status({fill:"green",shape:"dot",text:"equilibrating"});

    }

    return msg;
}
else {
    node.status({fill:"red",shape:"dot",text:"disabled"});

}

