// get the status of the request
var status = msg.statusCode;

var token = '';
msg.headers ={};

//let node = feedback;

switch(status){
    case 200:
        node.log("Secure restart");
        token = JSON.parse(msg.payload);
        token = 'Bearer '+token.access_token;
        msg.headers = {
            "Authorization": token,
            "Node-RED-Deployment-Type":"reload"
        }
//msg.payload ="";
        break;
    case 204:
        node.log("Secure without restart");
        break;
    case 400:
        node.warn("Bad request");
        break;
    case 401:
        node.warn("Not authorized");
        break;
    case 403:
        node.warn("Forbidden");
        break;
    case 404:
        node.log("Unsecure restart");
        msg.headers = {
            "Node-RED-Deployment-Type":"reload"
        }
        break;
    case 409:
        node.warn("Version mismatch");
        break;
    case 500:
        node.error("Server Error");
        break;
    default:
        node.warn("Unknown Error");
       break;
}

msg.payload = "";
return msg;
